const Board = () => {
    let classArray = ['tile'];
    classArray.push('tile'+tile.value);
    if(!tile.mergedInto){
        classArray.push(`position_${tile.row}_${tile.column}`);
    }
    if(tile.mergedInto){
        classArray.push('merged');
    }
    if(tile.isNew()){
        classArray.push('new');
    }
    if(tile.hasMoved()){
        classArray.push(`row_from_${title.fromRow()}_to_${title.toRow()}`)
